import logo from './logo.svg';
import './App.css';
import {useState, useEffect} from "react";

function App() {
  const [page, setPage] = useState(null)

  //Fetching data or authentication by using useEffect is known as 'side-effect'

  useEffect(() => {
    window.fetch(`https://graphql.contentful.com/content/v1/spaces/gah1iulwmgz2/`, {
      method: 'POST',
      headers:{
        'Content-Type': 'application/json',
        //Authenticate the request
        Authorization: 'Bearer IRtiTTFV0nv8MSRgvLJG1ArzsBPxSKshjas3L8ss6fo',
      },
      //send the GraphQL query
      body: JSON.stringify({ query }),
    })
    .then((response) => response.json())
    .then(({ data, errors }) => {
      if(errors) {
        console.error(errors)
      }
      // rerender the entire component with new data
      setPage(data.pageCollection.items[0])

    })

  }, []);

  if(!page) {
    return <h2>Loading...</h2>
  }

  const query = `{
    "data": {
      "pageCollection": {
        "items": [
          {
            "title": "Test - React + Contentful ",
            "logo": {
              "url": "https://images.ctfassets.net/gah1iulwmgz2/1mWqZX7TZZ33XTEfEsWzhk/41a74e6a4c1f8e0100e4b8b0744d8720/gay-logo.png"
            }
          }
        ]
      }
    }
  }`


  return (
    <div className="App">
      <h1>EMPTY</h1>
    </div>
  );
}

export default App;
