import './App.css';
import { useState, useEffect } from "react";
import Navbar from './components/Navbar';
import ProductCard from './components/ProductCard';

//Contentful query
const query = `{
      pageCollection{
    items{
      title
      logo{
        url
      }

  }
}

}`

function App() {
  const [page, setPage] = useState(null)

  //Fetching data or authentication by using useEffect is known as 'side-effect'

  useEffect(() => {
    window
      .fetch(`https://graphql.contentful.com/content/v1/spaces/gah1iulwmgz2/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer IRtiTTFV0nv8MSRgvLJG1ArzsBPxSKshjas3L8ss6fo",
        },
        body: JSON.stringify({ query }),
      })
      .then((response) => response.json())
      .then(({ data, errors }) => {
        if (errors) {
          console.error(errors);
        }

        setPage(data.pageCollection.items[0]);
      });
  }, []);

  if (!page) {
    return "Loading...";
  }

  return (
    <div className="App">
      <Navbar />
      <ProductCard logo={page.logo.url} title = {page.title} />

    </div>
  );
}

export default App;
